mapper.update:
update sys_permission
set name = #{name,jdbcType=VARCHAR},
  description = #{description,jdbcType=VARCHAR},
  url = #{url,jdbcType=VARCHAR},
  pid = #{pid,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, name, description, url, pid
from sys_permission
where id = #{id,jdbcType=BIGINT}
